var peers = ['https://livecodestream-eu.herokuapp.com/gun'];
var opt = { peers: peers, localStorage: false, radisk: false };
const gun = Gun(opt);

gun.get(window.location.hostname).on(function (data, key) {
    console.log(data.id);
    console.log(data.action);

    if (data.action == "call") {
        addCaller(data);
    }
    if (data.action == "answer") {

    }
    if (data.action == "hangup") {
        removeCaller(data);
    }
}, true)

function addCaller(data) {
    beep();

    let div = document.getElementById(data.id);
    if (div == undefined) {
        let grid = document.getElementById("callers");
        grid.insertAdjacentHTML('beforeend', createCard(data.id));
        // div = document.createElement("div");
        // div.id = data.id;
        // div.innerHTML = `<a href="#" onclick="answer(this)">Answer ${data.id}</a>`;
        // grid.appendChild(div);
    }
}

function removeCaller(data) {
    let caller = document.getElementById(data.id)
    if (caller) {
        caller.parentElement.removeChild(caller);
    }
}

function answer(id) {
    gun.get(window.location.hostname).put({ id: id.id, action: "answer" });
    console.log("Answer call redirect to Meething");
    try {
        let element = document.getElementById(id.id);
        element.parentElement.removeChild(element);
    } catch (e) {
        console.log("Caller is not here anymore")
    }
}

function reject(id) {
    gun.get(window.location.hostname).put({ id: id.id, action: "reject" });
    console.log("Answer call redirect to Meething");
    try {
        let element = document.getElementById(id.id);
        element.parentElement.removeChild(element);
    } catch (e) {
        console.log("Caller is not here anymore")
    }
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    try {
        snd.play();
    } catch (e) {
        console.log(e);
    }
}

function createCard(id) {
    return `
    <div id="${id}" class="contact-card">
    <div class="contact-card__header-image" style="background-image: url('https://picsum.photos/400/200')";>
      <div class="contact-card__avatar" style="background-image: url('https://api.adorable.io/avatars/285/${id}@adorable.io.png');"></div>
    </div>
      <p class="contact-card__name">${id}</p>
    <ul>
      <li class="contact-card__link contact-card__link--email green"><a title="Answer" href="#" onclick="answer(${id})">Answer</a></li>
      <li class="contact-card__link contact-card__link--mobile red"><a title="Reject" href="#" onclick="reject(${id})">Reject</a></li>
  </div>`
}

